import React, { Component } from 'react';
import './App.css';
import { Layout, Header, HeaderTabs, Tab, Content } from 'react-mdl';
import Home from './components/Home';
import About from './components/About';
import Works from './components/Works';
import Contact from './components/Contact';
import Footer from './components/Footer';


class App extends Component {
    constructor(props) {
        super(props)
        this.myRef = React.createRef();
        this.state = { activeTab: 0 };
    }

    scrollToDiv = () => {
        alert(this.myRef.offsetTop)

        window.scrollTo({
            top: this.myRef.offsetTop,
            behavior: 'smooth'
          });
    }

  render() {
    return (
      <div style={{height: '100vh', position: 'relative'}}>
      <Home/>
    <Layout fixedHeader fixedTabs style={{background: 'white'}}>
        <Header  style={{background: 'white'}}>
            <HeaderTabs ripple activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })}>

                <Tab onClick={this.scrollToDiv} style={{background: 'white', color: 'black'}}>About</Tab>
                <Tab style={{background: 'white', color: 'black'}}>Works</Tab>
                <Tab style={{background: 'white', color: 'black'}}>Contact</Tab>
            </HeaderTabs>
        </Header>
        <Content>
            <div className="page-content">
             <About />
             <Works ref={this.myRef}/>
             <Contact />
             <Footer />
            </div>
        </Content>
    </Layout>
    </div>
    );
  }
}

export default App;
